import React, {useState} from 'react';
import './styles.css';
import { Logo } from '../../themes';
import { GreenButton } from '../../components/Buttons';
import { Link } from 'react-router-dom';
import ErrorPage from '../ErrorPage';
import axios from 'axios';

const Login = () => {
	const [email, setEmail] = useState('');
	const [password, setPassword] = useState('');
	const [signUpText, setSignUpText] = useState('Login');
	const [errorText, setErrorText] = useState('');


	const validate = () => {
		const data = {
			email: email,
			password: password,
		};
		setSignUpText(
			<div
				style={{
					display: 'flex',
					justifyContent: 'center',
					alignItems: 'center',
				}}
			>
				<div className='rotate'></div>
			</div>,
		);
		console.log(data);

		axios({
			method: 'post',
			url: `/login`,
			data: data,
		})
			.then(res => {
				setSignUpText('Login')
				if (res.data) {

				}
				console.log(res);
			})
			.catch(err => {
				console.log(err.response);
				setSignUpText('Login')

			});
	};

	return (
		<div className='login-container'>

			<div className='login-content-container'>
				<img src={Logo.Asset_default_logo} alt='logo' />
				<h1>Welcome to Farm Hero</h1>
				<p>
					<Link to='/signup'>
						<span className='span-login'>Sign Up</span>
					</Link>{' '}
					Now
				</p>
				<div className='form-content-container'>
					<form>
						<div className='form-content'>
							<label htmlFor='email'>E-mail</label>
							<br />
							<input
								type='email'
								name='email'
								value={email}
								onChange={e =>
									setEmail(e.target.value)
								}
								placeholder='Enter Email'
							/>
						</div>
						<div className='form-content'>
							<label htmlFor='password'>
								Password{' '}
							</label>
							<br />
							<input
								type='password'
								name='password'
								placeholder='Enter Password'
								value={password}
								onChange={e =>
									setPassword(
										e.target.value,
									)
								}
							/>
						</div>
					</form>

						<div className='login-green' onClick={validate}>
							<GreenButton customLabel='Login' />
						</div>

					<Link to='/reset-pass'>
						<p className='forget-password'>
							Did you forget your password?
						</p>
					</Link>
				</div>
			</div>
		</div>
	);
};

export default Login;
