import React, { useEffect, useState } from 'react';
import { generalIcons } from '../../../themes'
import { Dropdown } from '../../../Cards'
import './styles.css'

const Navbar = () => {
	const [dropdownActive, setDropdownActive] = useState(false);
	const showDropdown = () => {
		setDropdownActive(!dropdownActive);
	};
	useEffect(() => {
		const dropdown = document.querySelector(
			'.navbar-dropdown-container',
		);
		dropdownActive
			? (dropdown.style.display = 'block')
			: (dropdown.style.display = 'none');
	}, [showDropdown]);
      return (
		<div className='navbar-container'>
			<div className='navbar-content'>
				<div className='navbar-notification'>
					<div className='notifier'>{'1'}</div>
					<img
						src={generalIcons.Asset_Notification}
						alt='notification'
					/>
				</div>
				<div className='navbar-notific-dropdown-container'>
					<Dropdown
						customStyle={{
							position: 'absolute',
							width: '150px',
							paddingLeft: '15px',
						}}
						customData={[
							'Profile',
							'Setting',
							'Logout',
						]}
					/>
				</div>
				<div className='navbar-profile'>
					<img
						src={generalIcons.Asset_Profile}
						alt='profile'
					/>
				</div>
				<div
					className='navbar-dropdown'
					onClick={showDropdown}
					style={{ cursor: 'pointer' }}
				>
					<img
						src={generalIcons.Asset_Arrow}
						alt='dropdown'
					/>
				</div>
			</div>
			<div className='navbar-dropdown-container'>
				<Dropdown
					customStyle={{
						position: 'absolute',
						width: '150px',
						paddingLeft: '15px',
					}}
					customData={['Profile', 'Setting', 'Logout']}
				/>
			</div>
		</div>
	);
}

export default Navbar
